'use strict';

(function ($) {
  $(function () {
    // Stick the #nav to the top of the window
    var nav = $('#navigation');
    var isFixed = false;
    var $w = $(window);
    var margin = 100;

    function fixStyle() {
      var scrollTop = $w.scrollTop();
      var shouldBeFixed = scrollTop > margin;

      if (shouldBeFixed && !isFixed) {
        nav.addClass('scrolled-nav');
        nav.css({
          left: nav.offset().left,
          width: nav.width()
        });
        isFixed = true;
      } else if (!shouldBeFixed && isFixed) {
        nav.removeClass('scrolled-nav');
        isFixed = false;
      }
    }

    fixStyle();
    $w.scroll(fixStyle);
  });
})(jQuery);
"use strict";

// http://codepen.io/alexandredees/pen/wseEi modified
var interval;
(function ($) {

  function generateStar(canvas, ctx, starRadius) {
    ctx.beginPath();
    ctx.arc(starRadius + Math.random() * canvas.width, starRadius + Math.random() * canvas.height, starRadius * Math.random(), 0, Math.PI * 2, false);
    //ctx.arc(100, 30, starRadius, 0, Math.PI*2, false);

    var rand = Math.random();
    if (rand <= 0.5) {
      ctx.fillStyle = "rgba(255, 255, 255, 1)";
      ctx.shadowColor = "rgba(255, 255, 255, 0.5)";
      ctx.shadowBlur = 3;
    } else if (rand > 0.75) {
      ctx.fillStyle = "rgba(255, 254, 196, 1)";
      ctx.shadowColor = "rgba(255, 254, 196, 0.5)";
      ctx.shadowBlur = 4;
    } else {
      ctx.fillStyle = "rgba(192, 247, 255, 1)";
      ctx.shadowColor = "rgba(192, 247, 255, 0.5)";
      ctx.shadowBlur = 7;
    }
    ctx.fill();
  }

  $(function () {

    var canvas = document.getElementById("stars");
    var context = canvas.getContext("2d");

    onresize = function onresize() {
      canvas.width = canvas.clientWidth;
      canvas.height = canvas.clientHeight;
    };
    onresize();
    for (var i = 0; i < 50; i++) {
      generateStar(canvas, context, 3);
    }

    interval = setInterval(function (interval) {
      generateStar(canvas, context, 3);
    }, 200);

    setTimeout( // Stop sreating stars after 10s
    function () {
      clearInterval(interval);
    }, 10000);
  });
})(jQuery);
